(2, 1087, 'dans-folkloriques', 1, 1, 'Chansons Folkloriques', '2019-11-22', '2019-11-22', '00:00:00', '23:59:59', 1, '2019-11-21 23:00:00', '2019-11-22 22:59:59', 'Europe/Paris', 'Le choeur de Strasbourg interprète les chants alsaciens. Une experience unique et inoubliable !', 1, '2019-11-14', '00:00:00', 0, 0, 0, 0, NULL, '2019-11-14 13:48:18', '2019-11-15 02:20:50', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0),
(3, 1103, 'le-dragon-deugenie-schwartz', 1, 1, 'Le Dragon d’Eugénie Schwartz', '2019-11-26', '2019-11-26', '00:00:00', '23:59:59', 1, '2019-11-25 23:00:00', '2019-11-26 22:59:59', 'Europe/Paris', 'Mise en scène sur le thème de la liberté. Une oeuvre qui a marque sont temps et la fameuse \"Révolution des Bretzels\" de 1870. Donc une oeuvre essentielle.', 1, '2019-11-25', '00:00:00', 0, 0, 0, 0, NULL, '2019-11-14 23:44:16', '2019-11-15 03:55:59', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0),
(4, 1104, 'recital-de-gerhard-gnann-orgue', 1, 1, 'Récital de Gerhard Gnann / Orgue', '2019-11-30', '2019-11-30', '00:00:00', '23:59:59', 1, '2019-11-29 23:00:00', '2019-11-30 22:59:59', 'Europe/Paris', 'César Franck, Pablo Bruna, Wolfgang Amadeus Mozart, Debussy, Ravel et d\'autres encore. Que de belles oeuvres et que de bonnes raisons de venir.', 1, '2019-11-30', '00:00:00', 0, 0, 0, 0, NULL, '2019-11-14 23:46:47', '2019-11-15 03:56:19', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0),
